import React from 'react';
import styled from 'styled-components/macro';
import ArticleSelectorFlex from './ArticleSelector';

export default function CategoryVegetables ({articles, amounts}) {

    const vegetables = articles.filter((article) => article.articlecategory === "0");

    const amountArray = amounts.filter((amount) => amount.shoppinglistid === 1); // hardcoded!!

    return (
        <div>
            {vegetables.map(vegetable => <ArticleSelectorFlex key={vegetable.id} amount={amounts.amounts}>{vegetable.articledescription}</ArticleSelectorFlex>)}
        </div>
    )
}

