    //   fetch('http://shoppinglist-app.local/////////////////////////////////////////////////')
    //   .then(response => response.json())
    //   .then((data) => setAmounts(data))
    // }, []);

    const vegetables = articles.filter((article) => article.articlecategory === "0");

    // const itemsInShoppingList = amounts.filter((item) => item.shoppinglistid === shoppingListIdState);

    // const vegetablesAmountsArray = [];
    // .map((vegetable) => {
    //     if (amounts.shoppinglistid === {shoppingListId}) {
    //     } 
    // });
        const randomAmount = Math.floor(Math.random() * 4);

        const [amountState, setAmountState] = useState(randomAmount); 

        return (
            <div>
                {vegetables.map((vegetable) =>
                    <ArticleSelectorFlex 
                        key={vegetable.id}
                        description={vegetable.articledescription}
                        toChild={amountState}
                        sendToParent={setAmountState}>
                </ArticleSelectorFlex>)}
            </div>
        )

}
