import {useState, useEffect} from 'react';
import styled from 'styled-components/macro';

import vegetablesImage from './images/vegetables.jpg';
import coldCutsImage from './images/coldcuts.jpg';
import bodyCareImage from './images/bodyCare.jpg';

import Header from './Header';
import Body from './Body';
import CategoryButtonsPage from './CategoryButtonsPage';
import NavigationButtonArea from './NavigationButtonArea';

import CategoryVegetables from './CategoryVegetables';
import CategoryColdCuts from './CategoryColdCuts';
import CategoryBodyCare from './CategoryBodyCare';

function App() {

  const [shoppingListIdState, setShoppingListIdState] = useState(1); // selected shoppinglist, hardcoded for now

  const [articles, setArticles] = useState([]);

  useEffect(() => {
    fetch('http://shoppinglist-app.local/articles')
    .then(response => response.json())
    .then((data) => setArticles(data))
  }, []); 


  const [user, setUser] = useState([]);

  useEffect(() => {
    fetch('http://shoppinglist-app.local/user/data')
    .then(response => response.json())
    .then((data) => setUser(data))
  }, []);


  const [isOverlayVisible, setIsOverlayVisible] = useState(11);

  function makeAppear0() {
    console.log('test');
    setIsOverlayVisible(0);
  }
  function makeAppear1() {
    console.log('test');
    setIsOverlayVisible(1);
  }
  function makeAppear2() {
    console.log('test');
    setIsOverlayVisible(2);
  }
  function makeAppear3() {
    console.log('test');
    setIsOverlayVisible(3);
  }
  function makeAppear4() {
    console.log('test');
    setIsOverlayVisible(4);
  }
  function makeAppear5() {
    console.log('test');
    setIsOverlayVisible(5);
  }
  function makeAppear6() {
    console.log('test');
    setIsOverlayVisible(6);
  }
  function makeAppear7() {
    console.log('test');
    setIsOverlayVisible(7);
  }
  function makeAppear8() {
    console.log('test');
    setIsOverlayVisible(8);
  }
  function makeAppear9() {
    console.log('test');
    setIsOverlayVisible(9);
  }
  function makeAppear10() {
    console.log('test');
    setIsOverlayVisible(10);
  }
  function makeAppear11() {
    console.log('test');
    setIsOverlayVisible(11);
  }
  function makeAppear12() {
    console.log('test');
    setIsOverlayVisible(12);
  }
  function makeAppear13() {
    console.log('test');
    setIsOverlayVisible(13);
  }
  function makeAppear14() {
    console.log('test');
    setIsOverlayVisible(14);
  }

  return ( 
    <Body>
      <Header />
      <CategoryButtonsPage
      makeAppear0={makeAppear0}
      makeAppear1={makeAppear1}
      makeAppear2={makeAppear2}
      makeAppear3={makeAppear3}
      makeAppear4={makeAppear4}
      makeAppear5={makeAppear5}
      makeAppear6={makeAppear6}
      makeAppear7={makeAppear7}
      makeAppear8={makeAppear8}
      makeAppear9={makeAppear9}
      makeAppear10={makeAppear10}
      makeAppear11={makeAppear11}
      makeAppear12={makeAppear12}
      makeAppear13={makeAppear13}
      makeAppear14={makeAppear14}
       />

      <Overlay key="0" className={isOverlayVisible === 0 ? '' : 'hide_overlay'}>
        <OverlayHeader>
          <CategoryImage src={vegetablesImage} alt="vegetables" />
          <CategoryHeadline>
            vegetables
          </CategoryHeadline>
          <CloseThisCategoryButton
          onClick={() => {
            setIsOverlayVisible(null);
            }
          } /> 
        </OverlayHeader>
        <CategoryVegetables articles={articles} shoppingListIdState={shoppingListIdState}/>
      </Overlay>

      <Overlay key="6" className={isOverlayVisible === 6 ? '' : 'hide_overlay'}>
        <OverlayHeader>
          <CategoryImage src={coldCutsImage} alt="cold cuts" />
          <CategoryHeadline>
            cold cuts
          </CategoryHeadline>
          <CloseThisCategoryButton
          onClick={() => {
            setIsOverlayVisible(null);
            console.log({isOverlayVisible});
            }
          } /> 
        </OverlayHeader>
        <CategoryColdCuts articles={articles} shoppingListIdState={shoppingListIdState}/>
      </Overlay>

      <Overlay key="11" className={isOverlayVisible === 11 ? '' : 'hide_overlay'}>
        <OverlayHeader>
          <CategoryImage src={bodyCareImage} alt="body care &amps; cosmetics" />
          <CategoryHeadline>
            body care &amps; cosmetics
          </CategoryHeadline>
          <CloseThisCategoryButton
          onClick={() => {
            setIsOverlayVisible(null);
            console.log({isOverlayVisible});
            }
          } /> 
        </OverlayHeader>
        <CategoryBodyCare articles={articles} shoppingListIdState={shoppingListIdState}/>
      </Overlay>

      <NavigationButtonArea />

    </Body>
  );
}

export default App;

//// ◁ ×

const Overlay = styled.div`
  position: absolute;
  border-radius: 13px;
  top: 3%;
  color: ivory;
  left: 3%;
  right: 3%;
  bottom: 3%;
  justify-content: center;
  opacity: .95;
  z-index: 10;
  background-color: #111;
  transition: visibility 0s ease-in-out .5s;
  &.hide_overlay {
    visibility: hidden;
    transition: visibility 0s ease-in-out .5s;
  }
`

const OverlayHeader = styled.div`
  padding-bottom: 5px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 15vw;
  display: flex;
  color: #111;
  background-image: linear-gradient(180deg, green, #111);
  border-radius: 13px 13px 0 0;

`
 const CategoryImage = styled.img`
  height: 3.4rem;
  width: 3.4rem;
  border-right: 1px solid #333;
  border-radius: 13px 0 0 0;
  background-color: limegreen;
 `

 const CategoryHeadline = styled.div`
  color: #eee;
  justify-content: center;
  align-items: center;
  display: flex;
  flex-direction: column;
  font-size: 2.6rem;
  font-family: Monaco;
 `

 const CloseThisCategoryButton = styled.div`
  display: flex;
  border-left: 1px solid #333;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-image: linear-gradient(180deg, #e22, #111);
  border-radius: 0 13px 0 0;
  width: 3.4rem;
  height: 3.4rem;
  color: #ddd;
  font-size: 4rem;
  transition: background-image .5s ease-in-out .5s;
  &:hover {
    transition: background-image .5s ease-in-out .5s;
    background-image: linear-gradient(180deg, #f33, #111);
  }
  &::after {
    content: "◁";
  }
`
