import styled from 'styled-components/macro';


export default function ArticleSelector ({ amounts, articledescription }) {


    return (
        <div>            
            <ArticleSelectorFlex>
                <span className="articledescription">{articledescription}</span>
                <button onClick={() => {alert({articledespription}, minusButtonAlert)}}>-</button>
                <span className="amount">{amounts}</span>
                <button onClick={() => {alert(plusButtonAlert)}}>+</button>
            </ArticleSelectorFlex>
        </div>
    )
}




const ArticleSelectorFlex = styled.div`
    display: flex;
    justify-content: space-between;
    color: red;


`

