import React, { useState, useEffect } from 'react';
import ArticleSelectorFlex from './ArticleSelector';

export default function CategoryVegetables ({articles, shoppingListIdState, amounts}, props) {

    const vegetables = articles.filter((article) => article.articlecategory === "0");

    const randomAmount = Math.floor(Math.random() * 4);

    const [amountState, setAmountState] = useState(randomAmount); 

    const onchange = (data) => {
        setAmountState(data);
    }

    return (
        <div>
            {vegetables.map((vegetable) =>
                <ArticleSelectorFlex 
                    key={vegetable.id}
                    description={vegetable.articledescription}
                    data={amountState}
                    onchange={(e) => {onchange(e)}}>
            </ArticleSelectorFlex>)}
        </div>
    )

}
