import React, {useState, useEffect} from 'react';
import styled from 'styled-components/macro';


export default function ArticleSelector ({children, test }) {



    return (
        <div>            
            <ArticleSelectorFlex>
                <ArticleSelectorDescription>
                    {children}
                </ArticleSelectorDescription>
                <button onClick={() => {alert(minusButtonAlert)}}>-</button>
                <ArticleSelectorAmount>{test}10</ArticleSelectorAmount>
                <button onClick={() => {alert(plusButtonAlert)}}>+</button>
            </ArticleSelectorFlex>
        </div>
    )
}




const ArticleSelectorFlex = styled.div`
    display: flex;
    color: red;
    justify-content: space-between;
    height: 10vw;
    width: 70hw;
    color: #111;
    margin-bottom: 3px;
    border-radius: 5px;
    background-image: linear-gradient(ivory, papayawhip);
    padding-left: 5px;
`

const ArticleSelectorDescription = styled.span`
    display: flex;
    flex-flow: column wrap;
    justify-content: center;
    align-items: bottom;
    background-color: hotpink;
    width: 70%;
`
const ArticleSelectorAmount = styled.span`
    display: flex;
    flex-direction: column;
    width: 2rem;
    justify-content: center;
    align-items: center;

`
