import React, {useState} from 'react';
import styled from 'styled-components/macro';
import makeAnExportableFunctionOutOfThisHardcodedShoppingListItems from './data/hardcodedShoppinglistsContent';


export default function ProfilePage({listName, shoppingListId, userId, loggedIn, users, changeLoggedInState, appMode}) {

    const [loggedInUserId, setLoggedInUserId] = useState(0);

    const [loggedInUserName, setLoggedInUserName] = useState('');

    const [loggedInUserPassword, setLoggedInUserPassword] = useState('');

    // The data like amounts and prices are mocked, since the respective database table was misconfigured in the first place 
    // and thus does not work properly.

    const allOfTheShoppingLists = makeAnExportableFunctionOutOfThisHardcodedShoppingListItems();

    let shoppingListNamesOfThisUser = []; 


    function groupItemsByShoppingListId(arrayOfShoppingListObjects, property) {
        return arrayOfShoppingListObjects.reduce(function (acc, obj) {
            var key = obj[property];
            if (!acc[key]) {
                acc[key] = [];
                acc[key].push(Object.values(obj['listname']));
            }
            return acc;
        }, {});
    }

    // console.log(loggedInUserName);

    function checkIfUserIsRegistered() {
        const userNameIndexInUsers = element => element['username'] === loggedInUserName;
        console.log(userNameIndexInUsers);
        const userPasswordIndexInUsers = element => element['password'] === loggedInUserPassword;
        const userIndex = users[users.findIndex(userNameIndexInUsers)];
        console.log(userIndex);

        if (users.findIndex(userNameIndexInUsers) === users.findIndex(userPasswordIndexInUsers)) {
            changeLoggedInState(true);
            shoppingListNamesOfThisUser = groupItemsByShoppingListId(allOfTheShoppingLists, userIndex);

        } else {
            console.log('try again');

        }




    }

    const handleUserNameInput = event => {
        // if (event.target.value) 
        setLoggedInUserName(event.target.value);
    }

    const handleUserPasswordInput = event => {
        setLoggedInUserPassword(event.target.value);
    }



    return(
        <div>
            <ProfileForm>
                <h1>Please log in</h1>
                <label htmlFor="username" />
                <Input value="Charles Babbage" name="username" onChange={handleUserNameInput} required type="text" placeholder="please enter your name" />
                <label htmlFor="password" />
                <Input value="kermit" name="password" onChange={handleUserPasswordInput} required type="password" placeholder="please enter your password" />
                <SubmitButton onClick={() => checkIfUserIsRegistered()}>log in</SubmitButton>
            </ProfileForm>
            <LoggedInMessage className={loggedIn === true && appMode === 2 ? 'logged_in' : ''}>Hallo {users['username']}!</LoggedInMessage>
            <ChooseShoppingListH3 className={loggedIn === true && appMode === 2 ? 'logged_in' : ''}>Please choose a shopping list:</ChooseShoppingListH3>
            <section>
                {Object.values(shoppingListNamesOfThisUser).map((element) =>
                        <div>
                            <button>{element}</button>
                        </div>
                )};
            </section>
        </div>

    );
}

const ProfileForm = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const Input = styled.input`
    margin: 5px;
    background-color: ivory;
    border-radius: 5px;
    background-color: ivory;
    color: #111;
    font-size: 1.2rem;
`

const SubmitButton = styled.button`
    margin: 5px;
    border-radius: 5px;
    width: 100px;
    height: 40px;
    background-color: ivory;
    color: #111;
`

const LoggedInMessage = styled.h2`
    visibility: hidden;
    text-align: center;
    color: #111;
    &.logged_in {
        visibility: visible;
    }
`

const ChooseShoppingListH3 = styled.h3`
    visibility: hidden;
    text-align: center;
    color: #111;
    &.logged_in {
        visibility: visible;
    }
`
