import React, {useState} from 'react';
import styled from 'styled-components/macro';



export default function ShoppingCart() {

    const heinz = [
        ['a', 1, 'Bananen', 3],
        ['n', 2, 'Cola', 1],
        ['f', 3, 'Erdnüsse', 2],
        ['j', 4, 'Fladenbrot', 1],
        ['o', 5, 'Geranien', 1],
        ['u', 6, 'Klopapier', 10],
        ['a', 7, 'Limetten', 4],
        ['i', 8, 'Müsli', 1],
        ['f', 9, 'Schokolade', 3],
        ['g', 10, 'Zimsterne', 3]
    ];

    const [heinzState, setHeinzState] = useState(heinz);

    // const ketchup = heinz.sort();
    // console.log(ketchup);

    return (
        <div>
            <h1>Einkaufsliste</h1>
            <div>
                {heinzState.map((item) => 
                    <ShoppingCartItemFlex>
                        <Amount>{item[3]}</Amount>
                        <Description>{item[2]}</Description>
                        <PutIntoBasketButton>✓✔︎☑︎</PutIntoBasketButton>
                        <PutOnHoldButton>⏳</PutOnHoldButton>
                    </ShoppingCartItemFlex>
                )}
            </div>
            <button onClick={() => setHeinzState(heinz.sort())}>;)</button>

        </div>


    );
}


const ShoppingCartItemFlex = styled.div`
    height: 10vw;
    left: 5px;
    right: 5px;
    display: flex;
    margin: 5px;
    background-image: linear-gradient(135deg, papayawhip, ivory);
    border-radius: 3px;
    color: #111;
    align-items: center;
    padding-left: 5px;
`

const Amount = styled.div`
    width: 2rem;
    height: 2rem;
    background-image: linear-gradient(315deg, papayawhip, ivory);
    align-items: center;
    justify-content: center;
`

const Description = styled.span`
    width: 70%;
    height: 2rem;
    background-color: rgba(1, 1, 1, .1);
    border-radius: 3px;
    font-size: 1.2rem;
`

const PutIntoBasketButton = styled.button`
    width: 2rem;
    height: 1.9rem;
    background-image: linear-gradient(135deg, #111, green);
`

const PutOnHoldButton = styled.button`
    background-image: linear-gradient(135deg, #111, #e22);
    height: 1.9rem;
    width: 2rem;
`
