import {useState, useEffect} from 'react';
import styled from 'styled-components/macro';

import vegetablesImage from './images/vegetables.jpg';
import fruitImage from './images/fruit.jpg';

import Header from './Header';
import Body from './Body';
import CategoryButtonsPage from './CategoryButtonsPage';
import NavigationButtonArea from './NavigationButtonArea';
import CategoryVegetables from './CategoryVegetables';
import CategoryFruit from './CategoryFruit';

function App() {

  const [shoppingListIdState, setShoppingListIdState] = useState(1); // selected shoppinglist, hardcoded for now

  const [articles, setArticles] = useState([]);

  useEffect(() => {
    fetch('http://shoppinglist-app.local/articles')
    .then(response => response.json())
    .then((data) => setArticles(data))
  }, []); 


  const [user, setUser] = useState([]);

  useEffect(() => {
    fetch('http://shoppinglist-app.local/user/data')
    .then(response => response.json())
    .then((data) => setUser(data))
  }, []);


  const [isOverlayVisible, setIsOverlayVisible] = useState(false);

  return ( 
    <Body>
      <Header />
      <CategoryButtonsPage />
      <Overlay key='0' className={isOverlayVisible ? '' : 'hide_overlay'}>
        <OverlayHeader>
          <CategoryImage src={vegetablesImage} alt="vegetables" />
          <CategoryHeadline>
            Gemüse
          </CategoryHeadline>
          <CloseThisCategoryButton
          onClick={() => {
            setIsOverlayVisible(!isOverlayVisible);
            console.log({isOverlayVisible});
            }
          } /> 
        </OverlayHeader>
        <CategoryVegetables articles={articles} shoppingListIdState={shoppingListIdState}/>
      </Overlay>

      <Overlay key='1' className={isOverlayVisible ? '' : 'hide_overlay'}>
        <OverlayHeader>
          <CategoryImage src={fruitImage} alt="fruit" />
          <CategoryHeadline>
            Früchte
          </CategoryHeadline>
          <CloseThisCategoryButton
          onClick={() => {
            setIsOverlayVisible(!isOverlayVisible);
            console.log({isOverlayVisible});
            }
          } /> 
        </OverlayHeader>
        <CategoryFruit articles={articles} shoppingListIdState={shoppingListIdState}/>
      </Overlay>

      <NavigationButtonArea />

    </Body>
  );
}

export default App;

//// ◁ ×

const Overlay = styled.div`
  position: absolute;
  border-radius: 13px;
  top: 3%;
  color: ivory;
  left: 3%;
  right: 3%;
  bottom: 3%;
  justify-content: center;
  opacity: .95;
  z-index: 10;
  background-color: #111;
  transition: visibility .5s ease-in-out .5s;
  &.hide_overlay {
    visibility: hidden;
    transition: visibility .5s ease-in-out .5s;
  }
`

const OverlayHeader = styled.div`
  padding-bottom: 5px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 15vw;
  display: flex;
  color: #111;
  background-image: linear-gradient(180deg, green, #111);
  border-radius: 13px 13px 0 0;

`
 const CategoryImage = styled.img`
  height: 3.4rem;
  width: 3.4rem;
  border-right: 1px solid #333;
  border-radius: 13px 0 0 0;
  background-color: limegreen;
 `

 const CategoryHeadline = styled.div`
  color: #eee;
  justify-content: center;
  align-items: center;
  display: flex;
  flex-direction: column;
  font-size: 2.6rem;
  font-family: Monaco;
 `

 const CloseThisCategoryButton = styled.div`
  display: flex;
  border-left: 1px solid #333;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-image: linear-gradient(180deg, #e22, #111);
  border-radius: 0 13px 0 0;
  width: 3.4rem;
  height: 3.4rem;
  color: #ddd;
  font-size: 4rem;
  &:hover {
    background-image: linear-gradient(180deg, #f33, #111);
  }
  &::after {
    content: "◁";
  }
`
