import styled from 'styled-components/macro';


export default function ArticleSelector ({ amounts, articledescription }) {


    return (
        <div>            
            <ArticleSelectorFlex>
                <span className="articledescription">{articledescription}</span>
                <button onClick={() => {alert("This button lowers a variable value which will be sent to the database when the close button gets pressed. The value will be initiated at the time the overlay comes up, and get the value of the according useState.")}}>-</button>
                <span className="amount">{amounts}</span>
                <button onClick={() => {alert("Please click the minus button for further information")}}>+</button>
            </ArticleSelectorFlex>
        </div>
    )
}




const ArticleSelectorFlex = styled.div`
    display: flex;
    justify-content: space-between;
