import React, {useState} from 'react';
import styled from 'styled-components/macro';




export default function ShoppingCart({appMode, whichShoppingListIsChosen, articles}) {

    let preList = [];

    const [heinzState, setHeinzState] = useState(preList); //confusing at first glance, sry!

    const weekend_list = [
        ['a', 0, 'Bananen', 3],
        ['a', 1, 'Paprika', 2],
        ['n', 2, 'Cola', 1],
        ['f', 3, 'Erdnüsse', 2],
        ['j', 4, 'Fladenbrot', 1],
        ['o', 5, 'Geranien', 1],
        ['u', 6, 'Klopapier', 3],
        ['a', 7, 'Limetten', 4],
        ['i', 8, 'Müsli', 1],
        ['f', 9, 'Schokolade', 3],
        ['g', 10, 'Zimtsterne', 3]
    ];

    const office_list = [
        ['u', 0, 'Seife', 2],
        ['u', 1, 'Klopapier', 2]
    ];


    console.log(whichShoppingListIsChosen);

    whichShoppingListIsChosen === "Bürobedarf" ? office_list.map((item) => preList.push([item[0] + item[1], item[2], item[3]])) : weekend_list.map((item) => preList.push([item[0] + item[1], item[2], item[3]]));
    preList = preList.sort();

    console.log(preList);



    console.log(heinzState);

    return (
        <div>
            <ShoppingCartItemFlexArea className={appMode === 0 ? 'app_mode_0' : ''}>
                {heinzState.map((item) => 
                    <ShoppingCartItemFlex key={item[0] + item[1]} className={whichShoppingListIsChosen === 'nothing selected' ? 'hide_this_shoppingcart_item' : ''}>
                        <Amount>{item[2]}</Amount>
                        <Description>{item[1]}</Description>
                        <PutIntoBasketButton 
                        onChange={(event) => setHeinzState()}
                        onClick={() => {
                            const aux = heinzState.findIndex(element => element[0] === item[0]);
                            setHeinzState([...heinzState.slice(0, aux), ...heinzState.slice(aux + 1)]);
                        }}
                        >✓</PutIntoBasketButton>
                        <PutOnHoldButton
                        onChange={(event) => setHeinzState()}
                        onClick={() => {
                            const aux = heinzState.findIndex(element => element[0] === item[0]);
                            setHeinzState([...heinzState.slice(0, aux), ...heinzState.slice(aux + 1)]);
                        }}>⏳</PutOnHoldButton>
                    </ShoppingCartItemFlex>
                )}
                <ShoppingCartIsEmpty className={whichShoppingListIsChosen !== 'nothing selected' &&  heinzState.length !== 0 ? 'hide_empty_sign' : ''} />
            </ShoppingCartItemFlexArea>

        </div>


    );
}



const ShoppingCartItemFlexArea = styled.div`
    visibility: hidden;
    &.app_mode_0 {
        visibility: visible;
    }
`

const ShoppingCartItemFlex = styled.div`
    display: flex;
    left: 10px;
    right: 10px;
    margin: 10px 5px;
    height: 50px;
    box-shadow: 2px 2px 3px #333;
    border-radius: 5px;
    background-image: linear-gradient(135deg, papayawhip, ivory);
    align-items: center;
    padding-left: 5px;
    color: #111;
    &.hide_this_shoppingcart_item {
        display: none;
    }
`

const Amount = styled.div`
    height: 2rem;
    width: 2rem;
    background-image: linear-gradient(315deg, papayawhip, ivory);
    justify-content: center;
    align-items: center;
    padding-right: 5px;
    text-align: center;
    font-size: 1.5rem;
`

const Description = styled.span`
    height: 2rem;
    width: 70%;
    border-radius: 3px;
    padding-left: 5px;
    font-size: 1.3rem;
`

const PutIntoBasketButton = styled.button`
    margin-left: 5px;
    margin-right: 5px;
    height: 2.2rem;
    width: 2.2rem;
    border-radius: 50%;
    background-image: linear-gradient(135deg, #111, green);
    font-size: 1.2rem;
    color: ivory;
`

const PutOnHoldButton = styled.button`
    margin-right: 5px;
    height: 2.2rem;
    width: 2.2rem;
    border-radius: 50%;
    background-image: linear-gradient(135deg, #111, #e22);
`   

const ShoppingCartIsEmpty = styled.div`
    margin-top: 100px;
    margin-left: 35px;
    display: inline-block;
    height: 2rem;
    width: 300px;
    background-image: linear-gradient(170deg, orange, goldenrod);
    text-align: center;
    font-size: 1.5rem;
    color: #111;
    &::after {
        content: 'This shoppinglist is empty.'
    }
    &.hide_empty_sign {
        display: none;
    }
`
